dict.string:
use strict;
use warnings;

my @availableBrowsers = `./defaultbrowser -status available`;
chomp @availableBrowsers;
my %availableBrowsersHash;
$availableBrowsersHash{$_}++ for (@availableBrowsers);

my @currentBrowser = `./defaultbrowser -status current`;
chomp @currentBrowser;
my %currentBrowserHash;
$currentBrowserHash{$_}++ for (@currentBrowser);

my @supportedBrowsers = ("Chrome","Firefox","Safari","Canary","Opera","Chromium","SeaMonkey","FirefoxDeveloperEdition","Webkit");
my $idCounter = 1;
my $foundBrowser = 0;

print qq{<?xml version="1.0" encoding="UTF-8" ?>\n<items>\n};



if ("{query}" eq "") {
	foreach (@supportedBrowsers)
	{
	my $code = lc($_);
	my $browser = $_;
    	if (exists $availableBrowsersHash{$code}) {
			if (exists $currentBrowserHash{$code}){
				print qq{<item uid="$idCounter"><arg>CURRDEFAULT</arg><title>[Current Default] Set default browser to $browser</title><subtitle></subtitle><icon>images/$code.png</icon></item>\n};
			} else {
				print qq{<item uid="$idCounter"><arg>$code</arg><title>Set default browser to $browser</title><subtitle></subtitle><icon>images/$code.png</icon></item>\n};
			}
		}
	$idCounter ++;
	}
} 

if ("{query}" ne ""){
	foreach (@supportedBrowsers)
	{
	my $code = lc($_);
	my $browser = $_;
    	if ((index(lc($code), lc("{query}")) != -1) && (exists $availableBrowsersHash{$code})) {
			if (exists $currentBrowserHash{$code}){
				print qq{<item uid="$idCounter"><arg>CURRDEFAULT</arg><title>[Current Default] Set default browser to $browser</title><subtitle></subtitle><icon>images/$code.png</icon></item>\n};
			} else {
				print qq{<item uid="$idCounter"><arg>$code</arg><title>Set default browser to $browser</title><subtitle></subtitle><icon>images/$code.png</icon></item>\n};
			}$foundBrowser = 1;
		}
	$idCounter ++;
	}

	if ($foundBrowser == 0){
		print qq{<item uid="1000"><arg>NOTFOUND</arg><title>No such browser is installed on this system</title><subtitle>Please try again.</subtitle><icon>images/error.png</icon></item>\n};
	}
}
print "</items>\n";
